import 'package:flutter/material.dart';import 'package:flutter_app/dao/home_dao.dart';import 'package:flutter_app/model/common_model.dart';import 'package:flutter_app/model/grid_nav_model.dart';import 'package:flutter_app/model/home_model.dart';import 'package:flutter_app/model/sales_box_model.dart';import 'package:flutter_swiper/flutter_swiper.dart';import 'package:flutter_app/widget/grid_nav.dart';import 'package:flutter_app/widget/local_nav.dart';import 'package:flutter_app/widget/sub_nav.dart';import 'package:flutter_app/widget/sales_box.dart';import 'package:flutter_app/widget/loadding_container.dart';const APPBAR_SCROLL_OFFSET = 100;class HomePage extends StatefulWidget{  @override  _HomePage createState() {    // TODO: implement createState    return _HomePage();  }}class _HomePage extends State<HomePage>{  double appBarAloha = 0;  String resultString = "";  List<CommonModel> localNavList = [];  List<CommonModel> subNavList = [];  List<CommonModel> bannerList = [];  SalesBoxModel salesBoxModel;  bool _loading = true;  GridNavModel gridNavModel;  @override  void initState(){    super.initState();    _handleRefresh();  }  _onScroll(offset){    double alpha = offset / APPBAR_SCROLL_OFFSET;        if(alpha < 0){          alpha = 0;        }else if(alpha > 1){          alpha = 1;        }        setState(() {          appBarAloha = alpha;        });        print(appBarAloha);  }  Future<Null> _handleRefresh() async{    try{      HomeModel model = await HomeDao.fetch();      setState(() {        resultString = '请求成功';        gridNavModel = model.gridNav;        localNavList = model.localNavList;        subNavList = model.subNavList;        salesBoxModel = model.salesBox;        bannerList = model.bannerList;        _loading = false;      });    }catch(e){      setState(() {        resultString = e.toString();        _loading = false;      });    }    return null;  }  @override  Widget build(BuildContext context) {    // TODO: implement build    return Scaffold(      backgroundColor: Color(0xfff2f2f2),      body:LoadingContainer(isLoading: _loading, child:  Stack(        children: <Widget>[          MediaQuery.removePadding(              context: context,              removeTop: true,              child: RefreshIndicator(child: NotificationListener(                onNotification: (scrollNotification){                  if(scrollNotification is ScrollUpdateNotification && scrollNotification.depth == 0){                    _onScroll(scrollNotification.metrics.pixels);                  }                },                child: _listView              ),                  onRefresh: _handleRefresh)          ),          _appBar        ],      ),)    );  }  Widget get _appBar{    return Opacity(      opacity: appBarAloha,      child: Container(        height: 80,        decoration: BoxDecoration(color: Colors.white),        child: Center(          child: Padding(padding: EdgeInsets.only(top: 20),            child: Text('首页'),          ),        ),      ),    );  }  Widget get _listView{    return ListView(      children: <Widget>[       _banner,        Padding(          child:   LocalNav(localNavList: localNavList),          padding: EdgeInsets.fromLTRB(7, 4, 7, 4 ),        ),        Padding(          child:  GridNav(gridNavModel: gridNavModel),          padding: EdgeInsets.fromLTRB(7, 0, 7, 4 ),        ),        Padding(          child:  SubNav(subNavList: subNavList),          padding: EdgeInsets.fromLTRB(7, 0, 7, 4 ),        ),        Padding(          child:  SalesBox(salesBox: salesBoxModel),          padding: EdgeInsets.fromLTRB(7, 0, 7, 4 ),        )      ],    );  }  Widget get _banner{    return  Container(        height: 160,        child:   Swiper(          itemCount : bannerList.length,          autoplay: true,          itemBuilder: (BuildContext context, int index){            return GestureDetector(              onTap: (){              },              child: Image.network(bannerList[index].icon, fit: BoxFit.fill,),            );          },          pagination: SwiperPagination(),        )    );  }}